class LanguageSelector{
    setLang(lang){
        l=lang.toLowerCase();
        localStorage.setItem('lang',l);
        return!0
    }
    getLang(){
        var lang=localStorage.getItem('lang');
        if(lang!=null){
            return lang
        } else {
            return 'en'
        }
    }
    removeLang(){
        localStorage.removeItem('lang');
        return!0
    }
    loadLang(){
        var l=this.getLang();
        var strs=lang[l];
        return strs
    }
    parse(){
        var rtl=['ar','ur','fa','he','arc','az','dv','arabic','aramaic','azeri','maldivian','dhivehi','hebrew','kurdish','persian','urdu'];
        var lang=this.loadLang();
        var len=lang.length-1;
        var keys=Object.keys(lang);
        var values=Object.values(lang);
        for(var i=0;i<=keys.length-1;i++){
            $("body").children().each(function(){
                $(this).html( $(this).html().replace(keys[i],values[i]) )
            })
        }
        if(rtl.includes(this.getLang())){
            $("html").attr("lang", "ar");
            $("html").attr("dir", "rtl");            
            $("#langSelector option[value='ar']").attr("selected", "selected");
            $('link[href="bootstrap/css/bootstrap.min.css"]').attr('href', 'bootstrap/css/bootstrap.rtl.min.css');

        } else {
            $("html").attr("lang", "en");
            $("html").attr("dir", "ltr");
            $('link[href="bootstrap/css/bootstrap.rtl.min.css"]').attr('href', 'bootstrap/css/bootstrap.min.css');
            $("#langSelector option[value='en']").attr("selected", "selected");
        }
    }
}